document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".burger"),t=document.querySelector(".main-menu");e.addEventListener("click",()=>{e.classList.toggle("burger_active"),t.classList.toggle("main-menu_active")});const n=document.querySelector(".first-screen__content");let o=Math.max(document.body.scrollWidth,document.documentElement.scrollWidth,document.body.offsetWidth,document.documentElement.offsetWidth,document.body.clientWidth,document.documentElement.clientWidth);function d(){return!(o<=450)&&(window.pageYOffset<=200?(t.style.position="",t.style.top="",t.style.width="",!1):(t.style.position="fixed",t.style.top=0,t.style.width=n.offsetWidth+"px",!0))}d(),document.addEventListener("resize",()=>{d()}),document.addEventListener("scroll",()=>{d()})}));